import gtk import gtksheet
class MainWin(gtk.Window): def __init__(self): gtk.Window.__init__(self)
self.sheet = gtksheet.Sheet(columns=20, rows=20, title="GtkSheet Demo")
scrwin = gtk.ScrolledWindow() scrwin.add(self.sheet)
self.add(scrwin) self.show_all() self.set_size_request(400,400)
if __name__ == '__main__': MainWin() gtk.main()
